package DataType;

public class DataTypeBoolean {

	public DataTypeBoolean() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		float b = 10.0001f;
		boolean r = true;
		boolean s = false;
		if (a < b) {
			System.out.println(r);
		} 
		else 
		{
			System.out.println(s);
		}

	}

}
