package Loops;

public class ForLoopBreak {

	public ForLoopBreak() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 100; i++) {
			if (i == 98) {
				break;
			}
			System.out.println(i);
		}

	}

}
